styles:
.icon-music-outer {
  display: inline-block;
  width: 25px;
  height: 25px;
  position: fixed;
  right: 5px;
  top: 10px;
  font-size: 25px;
  color: #ffda51;
  z-index: 999;
  border-radius: 16px;
  /*border: solid #ffda51 2px;*/
}

.icon-music-outer img {
  width: 100%;
  height: 100%;
}

.forbid {
  display: inline-block;
  font-size: 25px;
  width: 25px;
  height: 25px;
}

.icon-forbidMusic {
  display: inline-block;
  font-size: 25px;
  width: 25px;
  height: 25px;
  color: #d0f2fc;
}

audio {
  position: absolute;
  left: -300px;
}

.activeMusic {
  transform: rotate(0);
  -webkit-animation: musicMove 2s infinite linear;
  animation: musicMove 2s infinite linear;
}

@-webkit-keyframes musicMove {
  0% {
    transform: rotate(0);
  }

  100% {
    transform: rotate(360deg);
  }
}

@keyframes musicMove {
  0% {
    transform: rotate(0);
  }

  100% {
    transform: rotate(360deg);
  }
}
